import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart
    const addToCartBtn = await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await addToCartBtn.click();

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.querySelector('#cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    const cartIcon = await page.waitForSelector('.cart', { timeout: 5000 });
    await cartIcon.click();

    // Wait for cart modal to appear
    await page.waitForSelector('.cart-modal', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Remove item from cart
    const removeBtn = await page.waitForSelector('.remove-item', { timeout: 5000 });
    await removeBtn.click();

    // Wait for cart to update
    await page.waitForFunction(() => {
      const cartCount = document.querySelector('#cartCount');
      return cartCount && cartCount.textContent === '0';
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

