import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.waitForSelector('.product-card:first-child .add-to-cart');
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const count = document.getElementById('cartCount').textContent;
      return count && parseInt(count) > 0;
    });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { state: 'visible' });

    // Take screenshot with cart open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Click remove button
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');

    // Wait for cart to update (count should be 0)
    await page.waitForFunction(() => {
      const count = document.getElementById('cartCount').textContent;
      return count && parseInt(count) === 0;
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Extract verification data
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const cartItems = await page.$$eval('.cart-item', items => items.length);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount,
        remainingItems: cartItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
