import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.waitForSelector('.add-to-cart', { state: 'visible' });
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart', { state: 'visible' });
    await page.click('.cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item', { state: 'visible' });
    await page.click('.remove-item');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart count is 0
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: parseInt(cartCount)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and click on a product to add to cart
    const productSelector = '.product-card:first-child .add-to-cart';
    await page.waitForSelector(productSelector, { timeout: 5000 });
    await page.click(productSelector);

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.querySelector('#cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 5000 });

    // Take screenshot with cart open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Remove item from cart
    const removeButton = await page.waitForSelector('.remove-item', { timeout: 5000 });
    await removeButton.click();

    // Wait for cart to update
    await page.waitForFunction(() => {
      const cartCount = document.querySelector('#cartCount');
      return cartCount && cartCount.textContent === '0';
    }, { timeout: 5000 });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const cartItems = await page.$$eval('.cart-item', items => items.length);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount,
        remainingItems: cartItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Electronics category to load products
    await page.waitForSelector('a[onclick*="electronics"]');
    await page.click('a[onclick*="electronics"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_category.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_category.png'));

    // Wait for products to load and click first "Add to Cart" button
    await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await page.click('.add-to-cart:first-of-type');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent === '1';
    });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');

    // Wait for cart modal to appear and click remove button
    await page.waitForSelector('.remove-item', { timeout: 5000 });
    await page.click('.remove-item');

    // Wait for cart to be empty
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent === '0';
    });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: await page.$eval('#cartCount', el => el.textContent)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
