import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart
    await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Verify cart count updated
    const cartCount = await page.textContent('#cartCount');
    if (cartCount !== '1') {
      throw new Error('Cart count did not update after adding item');
    }

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { state: 'visible', timeout: 3000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Remove item from cart
    await page.click('.remove-item');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty
    const finalCartCount = await page.textContent('#cartCount');
    if (finalCartCount !== '0') {
      throw new Error('Cart count did not reset to 0 after removal');
    }

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        initialCartCount: '0',
        afterAddCartCount: '1',
        finalCartCount: '0'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iPhone');
    await page.click('button.search-btn');

    // Wait for search results and add first item to cart
    await page.waitForSelector('.product-card', { timeout: 10000 });
    await page.click('.product-card:first-child .add-to-cart');

    await page.waitForTimeout(2000); // Wait for cart to update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 10000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');

    // Wait for cart to update
    await page.waitForTimeout(2000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify removal by checking cart count
    const cartCount = await page.textContent('#cartCount');
    const cartItems = await page.$$eval('.cart-item', items => items.length);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount,
        remainingCartItems: cartItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to remove item from cart",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.waitForSelector('.product-card:has-text("iPhone") .add-to-cart');
    await page.click('.product-card:has-text("iPhone") .add-to-cart');
    await page.waitForTimeout(1000);

    // Verify item was added by checking cart count
    await page.waitForSelector('#cartCount');
    const cartCount = await page.textContent('#cartCount');
    if (cartCount !== '1') throw new Error('Item was not added to cart');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');
    await page.waitForTimeout(1000);

    // Verify item was removed by checking cart count
    const updatedCartCount = await page.textContent('#cartCount');
    if (updatedCartCount !== '0') throw new Error('Item was not removed from cart');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    return {
      success: true,
      action: "Successfully added iPhone to cart, opened cart modal, and removed item",
      extractedData: {
        initialCartCount: cartCount,
        finalCartCount: updatedCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete remove item from cart task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
