import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Add an item to cart
    await page.waitForSelector('.add-to-cart', { timeout: 10000 });
    await page.click('.add-to-cart');
    console.log('Clicked "Add to Cart" button');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 10000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Step 2: Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 10000 });
    console.log('Cart modal opened');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal.png'));

    // Step 3: Remove item from cart
    await page.waitForSelector('.remove-item', { timeout: 10000 });
    await page.click('.remove-item');
    console.log('Clicked "Remove" button');

    // Wait for cart to update after removal
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent === '0';
    }, { timeout: 10000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_removed_from_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_removed_from_cart.png'));

    // Extract cart count for verification
    const finalCartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and removed item",
      extractedData: {
        initialCartCount: '0',
        finalCartCount: finalCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
