    // Click Electronics category in navigation
    await page.waitForSelector('a:contains("Electronics")');
    await page.click('a:contains("Electronics")');
    await page.waitForLoadState('networkidle');

    // Wait for products to load and click first product
    await page.waitForSelector('.product-item');
    await page.click('.product-item:first-child');
    await page.waitForLoadState('networkidle');

    // Add product to cart
    await page.waitForSelector('.add-to-cart');
    await page.click('.add-to-cart');

    // Wait for cart count to update
    await page.waitForSelector('#cartCount:has-text("1")');

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');

    // Wait for cart modal to appear and remove item
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');

    // Verify item was removed by checking cart count
    await page.waitForSelector('#cartCount:has-text("0")');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_removed.png'));

    return {
      success: true,
      action: "Successfully added item to cart and removed it",
      extractedData: {
        initialCartCount: 0,
        finalCartCount: 0
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];

  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // First add an item to cart
    await page.waitForSelector('.add-to-cart:visible', { timeout: 5000 });
    await page.click('.add-to-cart:first-of-type');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent.trim() !== '0';
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal:visible', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item:visible', { timeout: 5000 });
    await page.click('.remove-item');

    // Wait for cart to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent.trim() === '0';
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Extract verification data
    const cartCount = await page.textContent('#cartCount');
    const extractedData = {
      finalCartCount: cartCount.trim()
    };

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and removed item successfully",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete remove item from cart task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
