import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.waitForSelector('.add-to-cart');
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000); // Wait for cart update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Verify cart count updated
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    if (cartCount !== '1') {
      throw new Error('Item was not added to cart successfully');
    }

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');
    await page.waitForTimeout(1000); // Wait for removal animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty
    const finalCartCount = await page.$eval('#cartCount', el => el.textContent);
    if (finalCartCount !== '0') {
      throw new Error('Item was not removed from cart successfully');
    }

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        initialCartCount: '0',
        afterAddCartCount: cartCount,
        finalCartCount: finalCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // First add an item to cart
    console.log('Looking for add to cart buttons...');
    await page.waitForSelector('.add-to-cart, [class*="add-to-cart"], button:has-text("Add to Cart")', { timeout: 5000 });

    // Click the first available add to cart button
    await page.click('.add-to-cart, [class*="add-to-cart"], button:has-text("Add to Cart")');
    console.log('Clicked add to cart button');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 3000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    console.log('Opening cart modal...');
    await page.click('.cart, [class*="cart"], #cartCount');

    // Wait for cart modal to appear
    await page.waitForSelector('.remove-item, [class*="remove"], button:has-text("Remove")', { timeout: 3000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Remove item from cart
    console.log('Removing item from cart...');
    await page.click('.remove-item, [class*="remove"], button:has-text("Remove")');

    // Wait for cart to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent === '0';
    }, { timeout: 3000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty
    const cartCount = await page.textContent('#cartCount');
    const cartStatus = cartCount === '0' ? 'empty' : 'not empty';

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount,
        cartStatus: cartStatus
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete remove item from cart task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart by finding first product and clicking its "Add to Cart" button
    await page.waitForSelector('.product-card');
    await page.click('.product-card:first-child .add-to-cart');
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal by clicking cart icon
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('#cart-modal');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item');
    await page.click('.remove-item');
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify cart is empty by checking cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const cartItems = await page.$$('.cart-item');

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: cartCount,
        remainingItems: cartItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
