import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput');

    // Type 'iP' in the search box
    await page.fill('#searchInput', 'iP');

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { timeout: 5000 });

    // Take a screenshot of the suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Check if any suggestions are displayed
    const suggestionsVisible = await page.isVisible('.search-suggestions');
    const suggestionsCount = await page.$$eval('.suggestion-item', items => items.length);

    // Return the extracted data and success status
    return {
      success: true,
      action: "Typed 'iP' in the search box and verified autocomplete suggestions appeared.",
      extractedData: {
        suggestionsVisible,
        suggestionsCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
