import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type 'iP'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iP');

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { state: 'visible' });

    // Take screenshot of the autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestions for verification
    const suggestions = await page.$$eval('.suggestion-item', items => items.map(item => item.textContent));

    return {
      success: true,
      action: "Typed 'iP' in the search box and verified autocomplete suggestions",
      extractedData: { suggestions },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

