import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Focus on the search input field and type 'iP'
    const searchInputSelector = '#searchInput';
    await page.waitForSelector(searchInputSelector);
    await page.fill(searchInputSelector, 'iP');

    // Take screenshot after typing in the search input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for the autocomplete suggestions to appear
    const suggestionsSelector = '.search-suggestions';
    await page.waitForSelector(suggestionsSelector, { state: 'visible' });

    // Take screenshot of the suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_suggestions_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_suggestions_visible.png'));

    // Extract suggestions for verification
    const suggestions = await page.$$eval(suggestionsSelector + ' .suggestion-item', items => {
      return items.map(item => item.textContent);
    });

    return {
      success: true,
      action: "Typed 'iP' in the search box and verified autocomplete suggestions appeared",
      extractedData: { suggestions },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
