import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible and enabled
    await page.waitForSelector('#searchInput', { state: 'visible', timeout: 5000 });

    // Clear any existing text in search input
    await page.fill('#searchInput', '');

    // Type 'iP' with delay to trigger autocomplete
    await page.type('#searchInput', 'iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { state: 'visible', timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { state: 'visible', timeout: 5000 });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestions: suggestions,
        suggestionCount: suggestions.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
