import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type 'iP'
    const searchInput = await page.waitForSelector('#searchInput');
    await searchInput.type('iP');

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { state: 'visible' });
    await page.waitForSelector('.suggestion-item', { state: 'visible' });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions.slice(0, 5) // First 5 suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

