import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type 'iP'
    await page.waitForSelector('#searchInput');
    const searchInput = await page.$('#searchInput');
    await searchInput.type('iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { timeout: 5000 });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract autocomplete suggestions data
    const suggestions = await page.$$eval('.suggestion-item', items => 
        items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestions: suggestions,
        suggestionsCount: suggestions.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search autocomplete test",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible and focused
    await page.waitForSelector('#searchInput', { state: 'visible' });
    await page.click('#searchInput');
    await page.waitForTimeout(500);

    // Type 'iP' in the search box
    await page.fill('#searchInput', 'iP');

    // Wait for autocomplete suggestions to appear using multiple strategies
    try {
      // Strategy 1: Wait for .search-suggestions element
      await page.waitForSelector('.search-suggestions', { state: 'visible', timeout: 5000 });
    } catch {
      // Strategy 2: Wait for .suggestion-item elements if .search-suggestions not found
      await page.waitForSelector('.suggestion-item', { state: 'visible', timeout: 5000 });
    }

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', elements => 
      elements.map(el => el.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.error('Failed to capture error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to test search autocomplete functionality",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

