import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type 'iP'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iP');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_ip.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_ip.png'));

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { state: 'visible' });
    await page.waitForSelector('.suggestion-item', { state: 'visible' });

    // Take screenshot with suggestions visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_suggestions_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_suggestions_visible.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions.filter(s => s.visible).map(s => s.text)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search autocomplete test",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

