    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible and enabled
    const searchInput = await page.waitForSelector('#searchInput', { 
      state: 'visible',
      timeout: 10000
    });

    // Type 'iP' slowly to trigger autocomplete
    await searchInput.type('iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear using multiple selector strategies
    const suggestionsContainer = await page.waitForSelector('.search-suggestions', {
      state: 'visible',
      timeout: 5000
    });

    // Wait for individual suggestion items to appear
    await page.waitForSelector('.suggestion-item', {
      state: 'visible',
      timeout: 3000
    });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'step_2_autocomplete.png'),
      fullPage: true 
    });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract autocomplete suggestions data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
