import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type 'iP'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iP');

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { state: 'visible' });
    await page.waitForSelector('.suggestion-item', { state: 'visible' });

    // Take screenshot after suggestions appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete_visible.png'));

    // Extract autocomplete suggestions
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        searchTerm: 'iP',
        suggestionsCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search autocomplete test",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible and enabled
    await page.waitForSelector('#searchInput', { state: 'visible' });
    await page.waitForTimeout(500); // Additional stability wait

    // Clear any existing text in search input
    await page.fill('#searchInput', '');

    // Type "iP" slowly to trigger autocomplete
    await page.type('#searchInput', 'i', { delay: 100 });
    await page.type('#searchInput', 'P', { delay: 100 });

    // Wait for autocomplete suggestions to appear using multiple strategies
    await page.waitForSelector('.search-suggestions', { state: 'visible', timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { state: 'visible', timeout: 5000 });

    // Take screenshot after suggestions appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete_visible.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions,
        visibleSuggestions: suggestions.filter(s => s.visible).length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible
    await page.waitForSelector('#searchInput', { state: 'visible' });

    // Type 'iP' into search input with delay to trigger autocomplete
    await page.fill('#searchInput', '');
    await page.type('#searchInput', 'iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear using multiple strategies
    await Promise.race([
      page.waitForSelector('.search-suggestions', { state: 'visible', timeout: 5000 }),
      page.waitForSelector('.suggestion-item', { state: 'visible', timeout: 5000 })
    ]);

    // Take screenshot after suggestions appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete search autocomplete test",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
