import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on search input
    const searchInput = await page.waitForSelector('#searchInput');
    await searchInput.click();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_focused.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_focused.png'));

    // Type 'iP' and wait for suggestions
    await searchInput.type('iP', { delay: 100 });
    await page.waitForTimeout(500); // Allow time for suggestions to appear

    // Wait for suggestions container and items
    await page.waitForSelector('.search-suggestions', { timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { timeout: 5000 });

    // Take screenshot of suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_suggestions_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_suggestions_visible.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete autocomplete test",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

