import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible using multiple selector strategies
    const searchInput = await page.waitForSelector('#searchInput, input[placeholder="Search Amazon"], .search-input', { 
      state: 'visible',
      timeout: 10000
    });

    // Clear any existing text and type "iP"
    await searchInput.click({ clickCount: 3 });
    await searchInput.press('Backspace');
    await searchInput.type('iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions, #searchSuggestions', { 
      state: 'visible',
      timeout: 5000
    });

    // Wait for suggestion items to load
    await page.waitForSelector('.suggestion-item', {
      state: 'attached',
      timeout: 3000
    });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionCount: suggestions.length,
        suggestions: suggestions.slice(0, 5) // First 5 suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.error('Failed to capture error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
