import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type 'iP'
    await page.waitForSelector('#searchInput', { timeout: 5000 });
    await page.fill('#searchInput', 'iP');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_ip.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_ip.png'));

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions', { timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { timeout: 5000 });

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_autocomplete_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_autocomplete_visible.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to test search autocomplete",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be ready using multiple selector strategies
    const searchInput = await page.waitForSelector('#searchInput, input.search-input', { state: 'visible' });

    // Clear any existing text and type 'iP' slowly to trigger autocomplete
    await searchInput.click({ clickCount: 3 });
    await searchInput.press('Backspace');
    await searchInput.type('iP', { delay: 100 });

    // Wait for autocomplete suggestions to appear
    await page.waitForSelector('.search-suggestions, #searchSuggestions', { state: 'visible', timeout: 5000 });

    // Wait a moment for suggestions to fully render
    await page.waitForTimeout(1000);

    // Take screenshot of autocomplete suggestions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item, [class*="suggestion"]', 
      items => items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestionsCount: suggestions.length,
        suggestions: suggestions.filter(s => s.visible).map(s => s.text)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible and click to focus
    const searchInput = await page.waitForSelector('#searchInput', { state: 'visible' });
    await searchInput.click();

    // Type 'iP' slowly to trigger autocomplete
    await searchInput.type('i', { delay: 100 });
    await searchInput.type('P', { delay: 100 });

    // Wait for autocomplete suggestions to appear using multiple strategies
    await page.waitForSelector('.search-suggestions', { state: 'visible', timeout: 5000 });
    await page.waitForSelector('.suggestion-item', { state: 'visible', timeout: 5000 });

    // Take screenshot after suggestions appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_autocomplete_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_autocomplete_visible.png'));

    // Extract suggestion data
    const suggestions = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Successfully typed 'iP' in search box and captured autocomplete suggestions",
      extractedData: {
        suggestions: suggestions,
        suggestionsCount: suggestions.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete autocomplete test",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
