import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the cart icon to open the cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming there's a cart modal that appears

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Click the '+' button to increase item quantity
    await page.waitForSelector('.quantity-btn'); // Assuming this is the selector for the '+' button
    await page.click('.quantity-btn');

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_increased.png'));

    // Extract updated cart data (e.g., quantity)
    const updatedQuantity = await page.$eval('.cart-item-quantity', el => el.textContent); // Assuming this is the selector for the quantity display

    return {
      success: true,
      action: "Updated cart item quantity",
      extractedData: { updatedQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

