import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the cart icon to open the cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming there's a modal that appears

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Increase item quantity by clicking the '+' button
    await page.waitForSelector('.quantity-btn'); // Assuming the button has this class
    await page.click('.quantity-btn');

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_increased.png'));

    // Extract the updated quantity to verify the task completion
    const updatedQuantity = await page.evaluate(() => {
      return document.querySelector('.quantity-display').innerText; // Assuming there's a display for quantity
    });

    return {
      success: true,
      action: "Updated cart item quantity successfully",
      extractedData: { updatedQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

