import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open Cart Modal
    await page.waitForSelector('.cart', { timeout: 5000 });
    await page.click('.cart');

    // Wait for the cart modal to be visible
    await page.waitForSelector('.cart-modal', { timeout: 5000 });

    // Take a screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal.png'));

    // Increase item quantity
    await page.waitForSelector('.quantity-btn', { timeout: 5000 });
    await page.click('.quantity-btn');

    // Take a screenshot after increasing the quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_updated.png'));

    // Validate if the quantity was updated successfully
    const quantityText = await page.$eval('.quantity-display', el => el.textContent);

    return {
      success: true,
      action: "Updated the cart item quantity",
      extractedData: { updatedQuantity: quantityText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
