import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the cart icon to open the cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Wait for the quantity button and click the '+' button to increase item quantity
    await page.waitForSelector('.quantity-btn');
    await page.click('.quantity-btn');

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_increased.png'));

    // Extract the updated quantity for verification
    const updatedQuantity = await page.$eval('.quantity-display', el => el.textContent);

    return {
      success: true,
      action: "Updated cart item quantity successfully",
      extractedData: { updatedQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the cart modal by clicking the cart icon
    await page.waitForSelector('.cart', { timeout: 5000 });
    await page.click('.cart');
    await page.waitForTimeout(1000); // Wait for modal to open

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Increase item quantity by clicking the '+' button
    await page.waitForSelector('.quantity-btn', { timeout: 5000 });
    await page.click('.quantity-btn');
    await page.waitForTimeout(1000); // Wait for quantity to update

    // Take screenshot after updating quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_updated.png'));

    // Verify the quantity has been updated
    const updatedQuantity = await page.$eval('.quantity-display', el => el.innerText);
    const success = parseInt(updatedQuantity) > 0; // Assuming quantity should be greater than 0

    return {
      success,
      action: "Increased item quantity in the cart",
      extractedData: { updatedQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

