import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the cart icon to open the cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming there's a cart modal that appears

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal.png'));

    // Wait for the quantity button to be visible and click the '+' button to increase quantity
    await page.waitForSelector('.quantity-btn');
    await page.click('.quantity-btn'); // Assuming this is the '+' button

    // Take screenshot after increasing the quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_increased.png'));

    // Extract the new quantity to verify the task completion
    const newQuantity = await page.$eval('.quantity-display', el => el.textContent); // Assuming there's an element showing the quantity

    return {
      success: true,
      action: "Updated cart item quantity successfully",
      extractedData: { newQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

