import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the cart icon to open the cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { state: 'visible' });

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal.png'));

    // Click the '+' button to increase item quantity
    await page.waitForSelector('.quantity-btn');
    await page.click('.quantity-btn');

    // Take screenshot after updating the quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_updated.png'));

    // Extract updated quantity for verification
    const updatedQuantity = await page.$eval('.quantity-display', el => el.textContent);

    return {
      success: true,
      action: "Updated cart item quantity",
      extractedData: { updatedQuantity },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

