import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Add first visible product to cart (simulate clicking ".add-to-cart" button)
    // Wait for a product card to be visible
    await page.waitForSelector('.product-card, .add-to-cart, text=Add to Cart', { timeout: 5000 });

    // Try to find an "Add to Cart" button
    let addToCartButton = await page.$('.add-to-cart');
    if (!addToCartButton) {
      // Try alternative: button with text "Add to Cart"
      addToCartButton = await page.$('button:has-text("Add to Cart")');
    }
    if (!addToCartButton) {
      // Try within a product card
      const productCard = await page.$('.product-card');
      if (productCard) {
        addToCartButton = await productCard.$('.add-to-cart');
      }
    }
    if (!addToCartButton) {
      throw new Error('Could not find Add to Cart button');
    }

    await addToCartButton.click();

    // Wait for cart count to update (should become 1)
    await page.waitForFunction(() => {
      const el = document.querySelector('#cartCount');
      return el && parseInt(el.textContent) > 0;
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // 2. Open cart modal (click on ".cart" in header)
    await page.waitForSelector('.cart', { timeout: 5000 });
    await page.click('.cart');

    // Wait for cart modal/dialog to appear
    // Try to wait for a quantity button or cart modal content
    await page.waitForSelector('.quantity-btn, button:has-text("+"), .cart-modal, .cart-items', { timeout: 5000 });

    // Take screenshot of cart modal open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_open.png'));

    // 3. Click the '+' button to increase quantity
    // Try several selectors for the plus/quantity button
    let plusButton = await page.$('.quantity-btn.plus');
    if (!plusButton) {
      plusButton = await page.$('button:has-text("+")');
    }
    if (!plusButton) {
      // Try a more generic selector
      const quantityBtns = await page.$$('.quantity-btn');
      for (const btn of quantityBtns) {
        const text = await btn.textContent();
        if (text && text.trim() === '+') {
          plusButton = btn;
          break;
        }
      }
    }
    if (!plusButton) {
      throw new Error('Could not find "+" quantity button in cart');
    }

    await plusButton.click();

    // Wait for quantity to update (look for quantity display in cart)
    // Assume there's an element with class "cart-item-quantity" or similar
    let newQuantity = null;
    await page.waitForFunction(() => {
      const qtyEl = document.querySelector('.cart-item-quantity, .cart-quantity, .quantity-value');
      if (!qtyEl) return false;
      const n = parseInt(qtyEl.textContent);
      return n >= 2;
    }, { timeout: 5000 }).catch(() => {});

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract cart quantity for verification
    let extractedQty = null;
    const possibleQtySelectors = [
      '.cart-item-quantity',
      '.cart-quantity',
      '.quantity-value',
      '.cart-modal .quantity',
      '.cart .quantity'
    ];
    for (const sel of possibleQtySelectors) {
      const el = await page.$(sel);
      if (el) {
        const txt = await el.textContent();
        if (txt && parseInt(txt)) {
          extractedQty = parseInt(txt);
          break;
        }
      }
    }
    // Fallback: try to get the value from an input[type=number] in cart
    if (!extractedQty) {
      const input = await page.$('.cart-modal input[type="number"], .cart input[type="number"]');
      if (input) {
        const val = await input.inputValue();
        if (val && parseInt(val)) {
          extractedQty = parseInt(val);
        }
      }
    }

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased item quantity by clicking the '+' button.",
      extractedData: {
        cartQuantity: extractedQty
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for a visible "Shop now" button (assumption: this adds an item to cart)
    await page.waitForSelector('button.cta-button', { state: 'visible', timeout: 5000 });
    // Click "Shop now" button to add an item to cart
    await page.click('button.cta-button');
    await page.waitForTimeout(800);

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_add_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_add_to_cart.png'));

    // Open cart modal by clicking on the cart icon (use .cart class, but as a div)
    await page.waitForSelector('div.cart', { state: 'visible', timeout: 5000 });
    await page.click('div.cart');
    await page.waitForTimeout(500);

    // Wait for the modal to appear (look for "Proceed to Checkout" button as anchor)
    await page.waitForSelector('button.checkout-btn', { state: 'visible', timeout: 5000 });

    // Take screenshot of cart modal open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Look for the first visible "+" quantity button in the cart modal
    // Try several selector strategies: text, aria-label, button, xpath
    let plusButton;
    // 1. Try using button text "+"
    plusButton = await page.$('//button[normalize-space(text())="+" and not(@disabled)]');
    if (!plusButton) {
      // 2. Try button with class "quantity-btn" and text "+"
      plusButton = await page.$('button.quantity-btn:has-text("+")');
    }
    if (!plusButton) {
      // 3. Try button with aria-label "Increase quantity"
      plusButton = await page.$('button[aria-label*="Increase"]');
    }
    if (!plusButton) {
      // 4. Try any button within the cart modal with "+"
      plusButton = await page.$('div[role="dialog"] button:has-text("+")');
    }
    if (!plusButton) {
      throw new Error("Could not find the '+' quantity button in cart modal.");
    }

    // Click the "+" button to increase quantity
    await plusButton.click();
    await page.waitForTimeout(700);

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_after_increase_quantity.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_after_increase_quantity.png'));

    // Extract the quantity value after increment. Try commonly used selectors:
    // Look for an input or span with a value or number near the "+" and "-" buttons
    let quantityValue = null;
    // 1. Try input[type="number"] within cart modal
    let quantityInput = await page.$('div[role="dialog"] input[type="number"]');
    if (quantityInput) {
      quantityValue = await quantityInput.inputValue();
    }
    if (!quantityValue) {
      // 2. Try span with class "cart-quantity" or similar
      let quantitySpan = await page.$('div[role="dialog"] .cart-quantity');
      if (quantitySpan) {
        quantityValue = await quantitySpan.textContent();
      }
    }
    if (!quantityValue) {
      // 3. Try getting text next to the "+" button
      const plusButtonBox = await plusButton.boundingBox();
      if (plusButtonBox) {
        // Try to find a span or input nearby (Â±50px in y-direction)
        const elements = await page.$$('div[role="dialog"] span, div[role="dialog"] input');
        for (const el of elements) {
          const box = await el.boundingBox();
          if (
            box &&
            Math.abs(box.y - plusButtonBox.y) < 50 &&
            ((await el.textContent()) || '').trim().match(/^\d+$/)
          ) {
            quantityValue = ((await el.textContent()) || '').trim();
            break;
          }
        }
      }
    }

    // Fallback: null if not found.
    // Also extract cart count in header
    let cartCount = null;
    try {
      const cartCountElem = await page.$('#cartCount');
      if (cartCountElem) {
        cartCount = (await cartCountElem.textContent()).trim();
      }
    } catch (e) {
      cartCount = null;
    }

    return {
      success: true,
      action: "Added item to cart, opened cart modal, increased quantity by clicking '+', and verified updated quantity.",
      extractedData: {
        cartModalQuantity: quantityValue,
        headerCartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first visible "Shop now" button (add-to-cart simulation)
    // Use text selector as previous attempts likely used .add-to-cart, try a different approach
    const shopNowBtn = await page.waitForSelector('button:has-text("Shop now")', { timeout: 5000 });
    await shopNowBtn.scrollIntoViewIfNeeded();
    await shopNowBtn.click();

    // Wait for cart count to update (ensure item is added)
    await page.waitForFunction(() => {
      const el = document.getElementById('cartCount');
      return el && parseInt(el.textContent, 10) > 0;
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_add_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_add_to_cart.png'));

    // Open the cart modal: click the cart icon in the header (use aria-label, icon, or XPath strategy)
    const cartIcon = await page.waitForSelector('.cart', { timeout: 5000 });
    await cartIcon.click();

    // Wait for the cart modal/dialog to appear (look for .cart-modal or fallback to modal by text)
    let cartModal;
    try {
      cartModal = await page.waitForSelector('.cart-modal, .cart-dialog, text=/Your Cart/i', { timeout: 4000 });
    } catch {
      // If not found, wait for a generic modal/dialog as fallback
      cartModal = await page.waitForSelector('[role="dialog"], [class*="cart"]', { timeout: 4000 });
    }

    // Take screenshot after cart opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Wait for the quantity '+' button
    // Try variants: [aria-label="Increase"], button[title="Increase"], button:has-text('+'), .quantity-btn.plus, or XPath
    let plusBtn = null;
    const plusSelectors = [
      'button[aria-label="Increase"]',
      'button[title="Increase"]',
      'button:has-text("+")',
      '.quantity-btn.plus',
      'button[aria-label*="add"], button[aria-label*="plus"]',
      '//button[contains(., "+")]',
      '//button[contains(@class,"quantity") and contains(., "+")]'
    ];
    for (const sel of plusSelectors) {
      try {
        if (sel.startsWith('//')) {
          plusBtn = await page.waitForSelector(`xpath=${sel}`, { timeout: 1200 });
        } else {
          plusBtn = await page.waitForSelector(sel, { timeout: 1200 });
        }
        if (plusBtn) break;
      } catch {}
    }
    if (!plusBtn) throw new Error('Could not find the quantity increase (+) button in the cart.');

    // Get the current quantity before clicking '+'
    // Try to find a sibling element, input, or text node showing quantity
    let quantityBefore = null;
    try {
      // Common: input[type="number"], .cart-qty, or text near the plus button
      const qtySelectors = [
        'input[type="number"].cart-qty',
        'input.quantity-input',
        '.cart-qty',
        '.quantity-value',
        'input[type="number"]',
        '.cart-item-quantity',
        'span:has-text("Qty") + span'
      ];
      for (const sel of qtySelectors) {
        try {
          const qtyElem = await page.$(sel);
          if (qtyElem) {
            quantityBefore = await qtyElem.inputValue?.() || await qtyElem.textContent();
            if (quantityBefore) break;
          }
        } catch {}
      }
      // As fallback, try to get text from the previous sibling of plusBtn
      if (!quantityBefore) {
        quantityBefore = await page.evaluate(btn => {
          if (btn.previousElementSibling) {
            return btn.previousElementSibling.value || btn.previousElementSibling.textContent;
          }
          return null;
        }, plusBtn);
      }
      if (quantityBefore) quantityBefore = quantityBefore.toString().trim();
    } catch {}

    // Click the '+' button to increase quantity
    await plusBtn.focus();
    await plusBtn.click();

    // Wait for the quantity value to increase (poll for a change)
    let quantityAfter = null;
    await page.waitForTimeout(600); // slight delay for UI update
    for (let i = 0; i < 10; i++) {
      quantityAfter = await (async () => {
        for (const sel of [
          'input[type="number"].cart-qty',
          'input.quantity-input',
          '.cart-qty',
          '.quantity-value',
          'input[type="number"]',
          '.cart-item-quantity'
        ]) {
          try {
            const qtyElem = await page.$(sel);
            if (qtyElem) {
              let val = await qtyElem.inputValue?.() || await qtyElem.textContent();
              if (val) return val.toString().trim();
            }
          } catch {}
        }
        // Fallback: try previous sibling of button again
        try {
          return await page.evaluate(btn => {
            if (btn.previousElementSibling) {
              return btn.previousElementSibling.value || btn.previousElementSibling.textContent;
            }
            return null;
          }, plusBtn);
        } catch {}
        return null;
      })();
      if (quantityAfter && quantityAfter !== quantityBefore) break;
      await page.waitForTimeout(250);
    }

    // Take screenshot after increasing quantity
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Prepare extracted data for verification
    const extractedData = {
      quantityBefore,
      quantityAfter
    };

    // Verify quantity actually increased
    let action;
    if (
      quantityAfter &&
      quantityBefore &&
      !isNaN(Number(quantityBefore)) &&
      !isNaN(Number(quantityAfter)) &&
      Number(quantityAfter) > Number(quantityBefore)
    ) {
      action = "Cart item quantity increased successfully using alternative selectors";
    } else {
      action = "Tried to increase cart item quantity, but could not verify numeric increase";
    }

    return {
      success: true,
      action,
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
