    // Find and click first "Add to Cart" button
    await page.waitForSelector('button.add-to-cart, .add-to-cart-button, [data-action="add-to-cart"]', { timeout: 5000 });
    await page.click('button.add-to-cart, .add-to-cart-button, [data-action="add-to-cart"]');
