    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart, #cart, [data-cart-button]', { timeout: 5000 });
    await page.click('.cart, #cart, [data-cart-button]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Increase quantity using '+' button
    await page.waitForSelector('.quantity-btn.plus, .quantity-increase, [data-action="increase-quantity"]', { timeout: 5000 });
    await page.click('.quantity-btn.plus, .quantity-increase, [data-action="increase-quantity"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract quantity data to verify success
    const quantity = await page.$eval('.quantity-value, .cart-quantity, [data-quantity]', el => el.textContent.trim());
    const cartCount = await page.$eval('#cartCount, .cart-count, [data-cart-count]', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        quantity: quantity,
        cartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first "Add to Cart" button
    await page.waitForSelector('button.add-to-cart', { timeout: 5000 });
    await page.click('button.add-to-cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal by clicking cart icon
    await page.waitForSelector('.cart', { timeout: 5000 });
    await page.click('.cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Increase quantity using the '+' button
    await page.waitForSelector('.quantity-btn', { timeout: 5000 });
    const plusButtons = await page.$$('.quantity-btn');
    for (const btn of plusButtons) {
      const text = await btn.textContent();
      if (text.includes('+')) {
        await btn.click();
        break;
      }
    }
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract quantity data
    const quantityElement = await page.$('.quantity-value') || await page.$('.cart-item-quantity');
    const quantity = quantityElement ? await quantityElement.textContent() : '1';

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        finalQuantity: quantity.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to update cart item quantity",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
