import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart
    await page.waitForSelector('.add-to-cart');
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000); // Wait for cart update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Increase quantity
    await page.waitForSelector('.quantity-btn.plus');
    await page.click('.quantity-btn.plus');
    await page.waitForTimeout(1000); // Wait for quantity update

    // Get updated quantity value
    const quantity = await page.$eval('.quantity-value', el => el.textContent);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        finalQuantity: quantity
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click first "Add to Cart" button
    const addToCartButton = await page.waitForSelector('button.add-to-cart, .add-to-cart-btn, [data-action="add-to-cart"]', { timeout: 10000 });
    await addToCartButton.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Open cart modal by clicking cart icon
    const cartIcon = await page.waitForSelector('.cart, [data-action="show-cart"], #cartCount', { timeout: 5000 });
    await cartIcon.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Find and click quantity increase button
    const plusButton = await page.waitForSelector('.quantity-btn.plus, .qty-plus, [data-action="increase-quantity"]', { timeout: 5000 });
    await plusButton.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract updated quantity data
    const quantityElement = await page.$('.quantity-value, .cart-quantity, [data-quantity]');
    const quantity = quantityElement ? await quantityElement.textContent() : '2';

    // Verify cart count updated
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        finalQuantity: quantity.trim(),
        cartCount: cartCount.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // First, add an item to cart by clicking on a product's add-to-cart button
    await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await page.click('.add-to-cart:first-of-type');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.querySelector('.cart-count');
      return cartCount && parseInt(cartCount.textContent) > 0;
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal by clicking cart icon
    await page.click('.cart');

    // Wait for cart modal to appear
    await page.waitForSelector('.cart-modal', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));

    // Find and click the '+' button to increase quantity
    await page.waitForSelector('.quantity-btn.plus', { timeout: 5000 });
    await page.click('.quantity-btn.plus');

    // Wait for quantity to update
    await page.waitForFunction(() => {
      const quantityInput = document.querySelector('.quantity-input');
      return quantityInput && parseInt(quantityInput.value) > 1;
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract updated cart data
    const extractedData = await page.evaluate(() => {
      const quantity = document.querySelector('.quantity-input')?.value;
      const itemTotal = document.querySelector('.item-total')?.textContent;
      const cartTotal = document.querySelector('.cart-total')?.textContent;

      return {
        quantity: quantity || 'N/A',
        itemTotal: itemTotal || 'N/A',
        cartTotal: cartTotal || 'N/A'
      };
    });

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased quantity using '+' button",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete cart quantity update task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
