import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for a product to add to cart
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iPhone');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Wait for search results and click first product
    await page.waitForSelector('.product-card');
    await page.click('.product-card:first-child');
    await page.waitForLoadState('networkidle');

    // Add product to cart
    await page.waitForSelector('.add-to-cart');
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000); // Wait for cart to update

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal');

    // Take screenshot of cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal.png'));

    // Increase quantity using '+' button
    await page.waitForSelector('.quantity-btn.plus');
    await page.click('.quantity-btn.plus');
    await page.waitForTimeout(500); // Wait for quantity update

    // Verify quantity increased
    const quantity = await page.$eval('.quantity-value', el => el.textContent);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_updated.png'));

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased quantity using '+' button",
      extractedData: {
        finalQuantity: quantity,
        itemInCart: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to update cart item quantity",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first if cart is empty
    const cartCount = await page.textContent('.cart-count');
    if (cartCount === '0') {
      // Find and click first "Add to Cart" button
      await page.waitForSelector('.add-to-cart', { timeout: 5000 });
      await page.click('.add-to-cart');

      // Wait for cart count to update
      await page.waitForFunction(() => {
        const count = document.querySelector('.cart-count');
        return count && count.textContent !== '0';
      });
    }

    // Open cart modal
    await page.click('.cart');

    // Wait for cart modal to appear
    await page.waitForSelector('.cart-modal', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_open.png'));

    // Increase quantity using '+' button
    await page.waitForSelector('.quantity-btn.plus', { timeout: 5000 });
    await page.click('.quantity-btn.plus');

    // Wait for quantity to update
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quantity_increased.png'));

    // Extract updated quantity data
    const quantity = await page.textContent('.quantity-value');
    const updatedCartCount = await page.textContent('.cart-count');

    return {
      success: true,
      action: "Added item to cart, opened cart modal, and increased item quantity",
      extractedData: {
        finalQuantity: quantity,
        finalCartCount: updatedCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

