import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart
    await page.waitForSelector('.add-to-cart');
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000); // Wait for cart update animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal.png'));

    // Increase quantity using '+' button
    await page.waitForSelector('.quantity-btn');
    const plusButtons = await page.$$('.quantity-btn');
    // Assuming the last quantity-btn is the '+' button based on typical cart design
    await plusButtons[plusButtons.length - 1].click();
    await page.waitForTimeout(1000); // Wait for quantity update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract quantity data to verify success
    const quantityElement = await page.$('.quantity-display');
    const quantityText = quantityElement ? await quantityElement.textContent() : 'N/A';

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        finalQuantity: quantityText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click first product's Add to Cart button
    await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await page.click('.add-to-cart:first-of-type');

    // Wait for cart to update and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 5000 });

    // Wait for cart to fully load and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Find and click the + button to increase quantity
    await page.waitForSelector('.quantity-btn.plus', { timeout: 5000 });
    await page.click('.quantity-btn.plus');

    // Wait for quantity update and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Extract updated quantity data
    const quantity = await page.$eval('.quantity-value', el => el.textContent);
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and increased quantity",
      extractedData: {
        updatedQuantity: quantity,
        cartItemCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete cart quantity update task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart
    await page.waitForSelector('.product-card:has-text("iPhone") .add-to-cart');
    await page.click('.product-card:has-text("iPhone") .add-to-cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Increase quantity
    await page.waitForSelector('.cart-item .quantity-btn:has-text("+")');
    await page.click('.cart-item .quantity-btn:has-text("+")');
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_quantity_increased.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_quantity_increased.png'));

    // Verify quantity update
    const quantity = await page.textContent('.cart-item .quantity-value');
    const itemName = await page.textContent('.cart-item .product-title');

    return {
      success: true,
      action: "Added iPhone to cart, opened cart modal, and increased quantity from 1 to 2",
      extractedData: {
        item: itemName?.trim(),
        quantity: quantity?.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to update cart item quantity",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
