import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set price range
    await page.waitForSelector('#minPrice');
    await page.fill('#minPrice', '50');
    await page.fill('#maxPrice', '200');
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after applying filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_filter.png'));

    // Extract meaningful data to verify task completion
    const filteredProducts = await page.$$eval('.product-item', items => {
      return items.map(item => ({
        title: item.querySelector('.product-title').innerText,
        price: item.querySelector('.product-price').innerText
      }));
    });

    return {
      success: true,
      action: "Applied price range filter and extracted filtered products",
      extractedData: { filteredProducts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set minimum and maximum price
    await page.waitForSelector('#minPrice'); // Assuming there's an input for min price
    await page.fill('#minPrice', '50'); // Input min price
    await page.waitForSelector('#maxPrice'); // Assuming there's an input for max price
    await page.fill('#maxPrice', '200'); // Input max price

    // Apply the price filter
    await page.click('.apply-filter'); // Click the apply filter button
    await page.waitForLoadState('networkidle');

    // Take screenshot after applying filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_filter.png'));

    // Verify that the filter is applied
    const filteredResults = await page.evaluate(() => {
      const results = Array.from(document.querySelectorAll('.product-item')); // Assuming product items have a class 'product-item'
      return results.map(item => ({
        title: item.querySelector('.product-title').textContent, // Assuming product title has a class 'product-title'
        price: parseFloat(item.querySelector('.product-price').textContent.replace('$', '')) // Assuming product price has a class 'product-price'
      }));
    });

    // Check if all filtered results are within the price range
    const isWithinPriceRange = filteredResults.every(product => product.price >= 50 && product.price <= 200);

    return {
      success: true,
      action: "Applied price range filter from 50 to 200",
      extractedData: { filteredResults, isWithinPriceRange },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

