import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set minimum price to 50
    await page.waitForSelector('input#minPrice');
    await page.fill('input#minPrice', '50');

    // Set maximum price to 200
    await page.waitForSelector('input#maxPrice');
    await page.fill('input#maxPrice', '200');

    // Apply the price filter
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after applying filters
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_filter.png'));

    // Extract meaningful data to verify task completion
    const filteredProducts = await page.$$eval('.product-item', items => items.map(item => ({
      title: item.querySelector('.product-title').innerText,
      price: item.querySelector('.product-price').innerText
    })));

    return {
      success: true,
      action: "Applied price range filter and retrieved filtered products",
      extractedData: { filteredProducts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

