import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set minimum price to 50
    await page.waitForSelector('input[type="number"][name="minPrice"]');
    await page.fill('input[type="number"][name="minPrice"]', '50');

    // Set maximum price to 200
    await page.waitForSelector('input[type="number"][name="maxPrice"]');
    await page.fill('input[type="number"][name="maxPrice"]', '200');

    // Apply the price filter
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after applying filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_apply_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_apply_filter.png'));

    // Check if the products are filtered correctly
    const filteredProducts = await page.$$eval('.product', products => products.map(p => p.innerText));
    const isPriceFiltered = filteredProducts.every(product => {
      const priceMatch = product.match(/\$(\d+(\.\d+)?)/);
      return priceMatch && (parseFloat(priceMatch[1]) >= 50 && parseFloat(priceMatch[1]) <= 200);
    });

    return {
      success: true,
      action: "Applied price range filter successfully",
      extractedData: { filteredProducts, isPriceFiltered },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
