import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set price range
    await page.fill('#minPrice', '50');
    await page.fill('#maxPrice', '200');
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after applying filters
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_filter.png'));

    // Verify that the price filter was applied
    const filteredProducts = await page.$$eval('.product', products => {
      return products.map(product => {
        const priceText = product.querySelector('.price').innerText;
        const price = parseFloat(priceText.replace(/[^0-9.-]+/g, ""));
        return price;
      });
    });

    const allInRange = filteredProducts.every(price => price >= 50 && price <= 200);

    return {
      success: true,
      action: "Applied price range filter from 50 to 200",
      extractedData: { allInRange },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

