import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Set price range
    await page.waitForSelector('#minPrice');
    await page.fill('#minPrice', '50');
    await page.fill('#maxPrice', '200');

    // Apply the price filter
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after applying filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_filter.png'));

    // Extract meaningful data to verify task completion
    const filteredProducts = await page.$$eval('.product-item', items => {
      return items.map(item => ({
        title: item.querySelector('.product-title').innerText,
        price: item.querySelector('.product-price').innerText
      }));
    });

    return {
      success: true,
      action: "Applied price range filter and extracted filtered products",
      extractedData: { filteredProducts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

