import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Input search term
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click search button
    await page.waitForSelector('button.search-btn');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_button_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_button_clicked.png'));

    // Set minimum price
    await page.waitForSelector('[name="undefined"]');
    await page.fill('[name="undefined"]', '50');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_min_price_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_min_price_set.png'));

    // Set maximum price
    await page.fill('[name="undefined"]', '200');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_max_price_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_max_price_set.png'));

    // Click apply filter button
    await page.waitForSelector('.apply-filter');
    await page.click('.apply-filter');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_apply_filter_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_apply_filter_clicked.png'));

    // Verify that the products are filtered correctly
    const filteredProducts = await page.$$eval('.product', products => products.length);
    const isPriceFiltered = filteredProducts > 0;

    return {
      success: true,
      action: "Applied price range filter successfully",
      extractedData: { filteredProductsCount: filteredProducts, isPriceFiltered },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
