import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be available and interact with it
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'products');

    // Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for the results to load
    await page.waitForLoadState('networkidle');

    // Set price range
    await page.fill('#minPrice', '50'); // Assuming there's an input with this ID for min price
    await page.fill('#maxPrice', '200'); // Assuming there's an input with this ID for max price

    // Click to apply filter
    await page.waitForSelector('.apply-filter');
    await page.click('.apply-filter');

    // Wait for the results to update
    await page.waitForLoadState('networkidle');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_price_filter_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_price_filter_applied.png'));

    // Verify if the price filter is applied successfully (pseudo-code for data extraction)
    const filteredProducts = await page.$$eval('.product-item', items => items.map(item => {
      const priceText = item.querySelector('.price').innerText;
      return parseFloat(priceText.replace(/[^0-9.-]+/g,"")); // Extract numeric price
    }));

    // Check if all products are within the price range
    const allInRange = filteredProducts.every(price => price >= 50 && price <= 200);

    return {
      success: true,
      action: allInRange ? "Price filter applied successfully and verified." : "Price filter applied but verification failed.",
      extractedData: { filteredProducts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
