import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open a modal (e.g., cart modal)
    await page.click('div.cart'); // Clicking on the cart icon to open the cart modal
    await page.waitForSelector('#cartModal', { state: 'visible' }); // Wait for the cart modal to be visible

    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_opened.png'));

    // Press the Escape key to close the modal
    await page.keyboard.press('Escape'); 
    await page.waitForSelector('#cartModal', { state: 'hidden' }); // Wait for the cart modal to be hidden

    // Take screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_closed.png'));

    // Verify if the modal is closed
    const isModalClosed = await page.isVisible('#cartModal') === false;

    return {
      success: true,
      action: "Closed the cart modal using the Escape key",
      extractedData: { modalClosed: isModalClosed },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
