import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open a modal (e.g., cart or product detail)
    await page.click('div.cart'); // Click on the cart to open the cart modal
    await page.waitForSelector('#cartModal'); // Wait for the cart modal to appear

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_opened.png'));

    // Press the Escape key to close the modal
    await page.keyboard.press('Escape');
    await page.waitForSelector('#cartModal', { state: 'hidden' }); // Wait for the modal to be closed

    // Take screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_closed.png'));

    return {
      success: true,
      action: "Closed the cart modal using the Escape key",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the product modal by simulating a click on a product (assuming a product is present)
    await page.waitForSelector('div.cart'); // Wait for the cart button
    await page.click('div.cart'); // Click on the cart to open the modal

    // Wait for the modal to be visible
    await page.waitForSelector('#cartModal', { state: 'visible' });

    // Take a screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_opened.png'));

    // Press the Escape key to close the modal
    await page.keyboard.press('Escape');

    // Verify the modal is no longer visible
    const isModalClosed = await page.isVisible('#cartModal');
    if (!isModalClosed) {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_closed.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_closed.png'));
    }

    return {
      success: true,
      action: "Closed the cart modal using the Escape key",
      extractedData: { modalClosed: !isModalClosed },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

