export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal (for demonstration, we'll assume a cart modal is opened)
    await page.click('.cart'); // Click on the cart icon to open the cart modal
    await page.waitForSelector('#cartModal', { state: 'visible' }); // Wait for the cart modal to be visible

    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_opened.png'));

