    await page.keyboard.press('Escape');
    await page.waitForSelector('#cartModal', { state: 'hidden' }); // Wait for the modal to be closed

    // Verify that the modal is closed
    const isModalClosed = await page.isVisible('#cartModal');
    if (!isModalClosed) {
      console.log("Modal closed successfully with Escape key.");
    }

    // Take screenshot after closing modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_closed.png'));

    return {
      success: true,
      action: "Closed the cart modal using the Escape key",
      extractedData: { modalClosed: !isModalClosed },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

