import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the product modal (simulating clicking on a product)
    await page.waitForSelector('div.cart', { timeout: 5000 });
    await page.click('div.cart'); // Click on cart to open modal
    await page.waitForSelector('#cartModal', { timeout: 5000 }); // Wait for the cart modal to be visible

    // Take screenshot of the modal open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_open.png'));

    // Press the Escape key to close the modal
    await page.keyboard.press('Escape');
    await page.waitForSelector('#cartModal', { state: 'hidden', timeout: 5000 }); // Wait for the modal to be hidden

    // Take screenshot to confirm modal is closed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_closed.png'));

    return {
      success: true,
      action: "Successfully opened the cart modal and closed it using the Escape key",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
