import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open a modal by clicking on a product link
    const productLinks = await page.$$('a:contains("Best Sellers"), a:contains("New Releases"), a:contains("Electronics")');
    if (productLinks.length > 0) {
      await productLinks[0].click();
      await page.waitForTimeout(1000);
    }

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal, #productModal, #cartModal', { state: 'visible', timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Press Escape key to close modal
    await page.keyboard.press('Escape');

    // Wait for modal to close and verify it's hidden
    await page.waitForSelector('.modal, #productModal, #cartModal', { state: 'hidden', timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_closed.png'));

    // Verify modal is no longer visible
    const modalVisible = await page.$eval('.modal, #productModal, #cartModal', 
      el => window.getComputedStyle(el).display !== 'none'
    ).catch(() => false);

    return {
      success: !modalVisible,
      action: "Opened and closed modal using Escape key",
      extractedData: {
        modalClosed: !modalVisible,
        modalType: await page.$eval('.modal, #productModal, #cartModal', el => el.id || el.className).catch(() => 'unknown')
      },
      screenshots,
      error: modalVisible ? 'Modal still visible after Escape key' : null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

