import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Try to open cart modal first
    try {
      await page.waitForSelector('.cart', { timeout: 3000 });
      await page.click('.cart');
      await page.waitForSelector('#cartModal, .modal', { timeout: 5000 });
    } catch {
      // If cart modal not found, try opening product modal
      await page.waitForSelector('a[onclick*="showProductDetail"]', { timeout: 3000 });
      await page.click('a[onclick*="showProductDetail"]:first-of-type');
      await page.waitForSelector('#productModal, .modal', { timeout: 5000 });
    }

    // Take screenshot with modal open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Press Escape key to close modal
    await page.keyboard.press('Escape');

    // Wait for modal to close
    await page.waitForSelector('#cartModal, #productModal, .modal', { state: 'hidden', timeout: 5000 });

    // Take final screenshot after closing modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_closed.png'));

    return {
      success: true,
      action: "Successfully opened and closed modal using Escape key",
      extractedData: {
        modalOpened: true,
        modalClosed: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

