import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Try to open cart modal first
    const cartButton = page.locator('.cart');
    if (await cartButton.isVisible()) {
      await cartButton.click();
      await page.waitForTimeout(1000); // Wait for modal animation

      // Check if cart modal opened
      const cartModal = page.locator('#cartModal, .modal').first();
      if (await cartModal.isVisible()) {
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_open.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_open.png'));

        // Press Escape key to close modal
        await page.keyboard.press('Escape');
        await page.waitForTimeout(500);

        // Verify modal is closed
        if (!(await cartModal.isVisible())) {
          await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_modal_closed.png') });
          screenshots.push(path.join(screenshotsDir, 'step_3_cart_modal_closed.png'));

          return {
            success: true,
            action: "Opened cart modal and closed it with Escape key",
            extractedData: { modalType: "cart" },
            screenshots,
            error: null
          };
        }
      }
    }

    // If cart modal didn't work, try product modal
    const productLinks = page.locator('a:has-text("iPhone"), a:has-text("Samsung"), a:has-text("Sony")');
    if (await productLinks.first().isVisible()) {
      await productLinks.first().click();
      await page.waitForTimeout(1000);

      // Check if product modal opened
      const productModal = page.locator('#productModal, .modal').first();
      if (await productModal.isVisible()) {
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal_open.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_product_modal_open.png'));

        // Press Escape key to close modal
        await page.keyboard.press('Escape');
        await page.waitForTimeout(500);

        // Verify modal is closed
        if (!(await productModal.isVisible())) {
          await page.screenshot({ path: path.join(screenshotsDir, 'step_3_product_modal_closed.png') });
          screenshots.push(path.join(screenshotsDir, 'step_3_product_modal_closed.png'));

          return {
            success: true,
            action: "Opened product modal and closed it with Escape key",
            extractedData: { modalType: "product" },
            screenshots,
            error: null
          };
        }
      }
    }

    throw new Error("Could not find or open any modal");

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
