import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open cart modal by clicking cart icon
    await page.waitForSelector('.cart');
    await page.click('.cart');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Take screenshot of open modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Press Escape key to close modal
    await page.keyboard.press('Escape');
    await page.waitForTimeout(500); // Wait for close animation

    // Verify modal is closed by checking it's not visible
    const modalClosed = await page.evaluate(() => {
      const modal = document.querySelector('#cartModal') || document.querySelector('.modal');
      return !modal || modal.style.display === 'none';
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_closed.png'));

    return {
      success: true,
      action: "Opened cart modal and closed it with Escape key",
      extractedData: {
        modalClosed: modalClosed
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

