import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Try to open cart modal first
    await page.waitForSelector('.cart', { timeout: 5000 });
    await page.click('.cart');

    // Wait for cart modal to appear with multiple selector strategies
    await Promise.race([
      page.waitForSelector('#cartModal', { timeout: 3000 }),
      page.waitForSelector('.modal', { timeout: 3000 }),
      page.waitForSelector('[class*="modal"]', { timeout: 3000 })
    ]);

    // Take screenshot with modal open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Press Escape key to close modal
    await page.keyboard.press('Escape');

    // Wait for modal to close using multiple verification methods
    await Promise.race([
      page.waitForSelector('#cartModal', { state: 'hidden', timeout: 3000 }),
      page.waitForSelector('.modal', { state: 'hidden', timeout: 3000 }),
      page.waitForFunction(() => {
        const modal = document.querySelector('.modal, #cartModal, #productModal');
        return !modal || modal.style.display === 'none';
      }, { timeout: 3000 })
    ]);

    // Take final screenshot after closing modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_closed.png'));

    return {
      success: true,
      action: "Opened cart modal and closed it using Escape key",
      extractedData: { modalClosed: true },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
