import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the product cards to load
    await page.waitForSelector('.product-card');

    // Extract product prices
    const prices = await page.$$eval('.product-card .product-price', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Take screenshot of the product prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_prices.png'));

    return {
      success: true,
      action: "Extracted all product prices",
      extractedData: { prices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extracting product prices from different categories
    const productPrices = [];

    // Wait for the product cards to load
    await page.waitForSelector('.product-card');

    // Extract prices from product cards
    const productCards = await page.$$('.product-card');
    for (const card of productCards) {
      const priceElement = await card.$('.product-price');
      if (priceElement) {
        const price = await page.evaluate(el => el.textContent.trim(), priceElement);
        productPrices.push(price);
      }
    }

    // Take screenshot after extracting prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted product prices from all categories",
      extractedData: { productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the product cards to load
    await page.waitForSelector('.product-card');

    // Extract all product prices
    const prices = await page.$$eval('.product-card .product-price', elements => 
      elements.map(el => el.innerText.trim())
    );

    // Extract category names
    const categories = await page.$$eval('.category-card', elements => 
      elements.map(el => el.innerText.trim())
    );

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_prices.png'));

    return {
      success: true,
      action: "Extracted all product prices and categories",
      extractedData: {
        prices,
        categories
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
