import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract all product prices
    const prices = await page.evaluate(() => {
      const priceElements = Array.from(document.querySelectorAll('.product-card .product-price, .category-card'));
      return priceElements.map(element => {
        return {
          price: element.textContent.trim(),
          productTitle: element.closest('.product-card') ? element.closest('.product-card').querySelector('.product-title')?.textContent.trim() : null
        };
      });
    });

    // Log extracted prices
    console.log(prices);

    // Take screenshot after extracting prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_prices.png'));

    return {
      success: true,
      action: "Extracted all product prices",
      extractedData: prices,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
